import React, { useState, useEffect } from 'react';
import { useCart } from '../../context/CartContext';
import './Navbar.css';

const Navbar = ({ onCartClick }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const { cart } = useCart();

  const cartItemsCount = cart.reduce((total, item) => total + item.quantity, 0);

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleMenuClick = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleLinkClick = () => {
    setIsMenuOpen(false);
  };

  return (
    <nav className={`navbar ${isScrolled ? 'scrolled' : ''}`}>
      <div className="navbar-container">
        <div className="navbar-brand">
          <a href="/" className="logo" onClick={handleLinkClick}>
            <span className="logo-text">Spice Haven</span>
          </a>
        </div>

        <button 
          className={`hamburger ${isMenuOpen ? 'active' : ''}`}
          onClick={handleMenuClick}
          aria-label="Toggle menu"
        >
          <span></span>
          <span></span>
          <span></span>
        </button>

        <div className={`navbar-menu ${isMenuOpen ? 'active' : ''}`}>
          <a href="/products" className="nav-link" onClick={handleLinkClick}>Products</a>
          <a href="/categories" className="nav-link" onClick={handleLinkClick}>Categories</a>
          <a href="/about" className="nav-link" onClick={handleLinkClick}>About</a>
          <div className="nav-buttons">
            <button 
              className="cart-button" 
              onClick={onCartClick}
              aria-label="Open cart"
            >
              ðŸ›’
              {cartItemsCount > 0 && (
                <span className="cart-count">{cartItemsCount}</span>
              )}
            </button>
            <a href="/login" className="btn btn-outline" onClick={handleLinkClick}>Login</a>
            <a href="/register" className="btn btn-primary" onClick={handleLinkClick}>Register</a>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
